package com.syntax.class01;

public class DataTypesAndVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * byte - data type
		 * variable - variable/box/container name
		 * = - assign
		 * 10 - value
		 */
		// stroing whole numbers in variables
		byte variable=10;
		short variable1=100;
		int var3=10000;
		long var4=10000000;
		// stroing numbers with decimals
		float myFloat=10.99f;
		double myDouble=100.99;
		
		//storing single character
		char singleLetter='O';
		char specialCharacter='$';
		
		// store boolean values: yes or no
		
		boolean myBoo=true;
		boolean myBoo1=false;
	}

}
